// MAP_LogRecepcion — HU1 (robusto)
// Lee payload desde $json, $json.body, $json.query o $json.formData
// Parsea "respuestas" si viene como string. No muta el payload original.

function pickPayload(src) {
  if (!src || typeof src !== 'object') return {};
  if (src.alumno_nombre || src.alumno_email || src.respuestas) return src;
  if (src.body && (src.body.alumno_nombre || src.body.alumno_email || src.body.respuestas)) return src.body;
  if (src.query && (src.query.alumno_nombre || src.query.alumno_email || src.query.respuestas)) return src.query;
  if (src.formData && (src.formData.alumno_nombre || src.formData.alumno_email || src.formData.respuestas)) return src.formData;
  return src;
}

function asArrayMaybeParsed(val) {
  if (Array.isArray(val)) return val;
  if (typeof val === 'string') {
    try { const parsed = JSON.parse(val); return Array.isArray(parsed) ? parsed : []; }
    catch { return []; }
  }
  return [];
}

function maskName(n) {
  if (!n) return "AlumnoXX";
  const p = String(n).trim().split(" ")[0] || "Alumno";
  return p + "XX";
}

function maskEmail(e) {
  const s = String(e||"").trim();
  if (!s.includes("@")) return "anon@masked";
  const [u, d] = s.split("@");
  const mu = u.length <= 2 ? u[0] + "*" : u[0] + "*".repeat(Math.max(1, u.length - 2)) + u.slice(-1);
  return `${mu}@${d}`;
}

const raw = $json || {};
const payload = pickPayload(raw);

const nombre = String(payload.alumno_nombre || "").trim();
const email  = String(payload.alumno_email  || "").trim();
const respuestas = asArrayMaybeParsed(payload.respuestas);

const omitidas_ids = [];
for (const r of respuestas) {
  const marcada = (r && typeof r.marcada !== 'undefined') ? String(r.marcada).trim() : "";
  if (marcada === "") omitidas_ids.push(String(r.pregunta_id || "").toUpperCase());
}

const logEntry = {
  status: "recepcion_ok",
  alumno: maskName(nombre),
  email: maskEmail(email),
  respuestas_count: respuestas.length,
  omitidas_count: omitidas_ids.length,
  omitidas_ids,
  keys_detectadas: Object.keys(payload),
  at: new Date().toISOString()
};

// Consola (si usas Executions lo verás) + embebido para evidencias
console.log("[HU1] Recepción payload", logEntry);

return [{
  json: {
    ...raw,                // NO tocamos el payload real del webhook
    __log_masked: logEntry // Resumen seguro para ver en Output
  }
}];

// HU2 - Normalize & Validate (robusto)
// Soporta payload en: $json, $json.body, $json.query, $json.formData
// Parsea respuestas si vienen como string. Normaliza keys y valores.
// CA2 – Pregunta vacía: log "pregunta_vacia_normalizada": true

function isEmail(e) {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(e||"").trim());
}

function normalizeKeys(obj) {
  const out = {};
  for (const k of Object.keys(obj || {})) {
    out[k.trim().toLowerCase()] = obj[k];
  }
  return out;
}

function pickPayload(src) {
  // intenta distintas fuentes comunes del Webhook
  if (src && (src.alumno_nombre || src.respuestas || src.alumno_email)) return src;
  if (src?.body && (src.body.alumno_nombre || src.body.respuestas || src.body.alumno_email)) return src.body;
  if (src?.query && (src.query.alumno_nombre || src.query.respuestas || src.query.alumno_email)) return src.query;
  if (src?.formData && (src.formData.alumno_nombre || src.formData.respuestas || src.formData.alumno_email)) return src.formData;
  return src || {};
}

const raw = $json || {};
let payload = pickPayload(raw);
payload = normalizeKeys(payload);

// Asegurar que "respuestas" sea un array
let respuestas = payload.respuestas;
if (typeof respuestas === 'string') {
  try { respuestas = JSON.parse(respuestas); } catch (e) { respuestas = []; }
}
if (!Array.isArray(respuestas)) respuestas = [];

// Extraer campos (después de normalizar keys)
const nombre = String(payload.alumno_nombre || "").trim();
const email  = String(payload.alumno_email  || "").trim();

// Validación
if (!nombre || !isEmail(email)) {
  return [{
    json: {
      error: {
        code: "VALIDATION_ERROR",
        message: "Faltan datos del alumno (nombre/email) o email inválido.",
        details: { alumno_nombre: !!nombre, alumno_email: isEmail(email) }
      },
      _meta: { valid: false, source_hint: Object.keys(raw) }
    }
  }];
}

// Normalizar respuestas: IDs y alternativas a MAYÚSCULAS, vacías => NO_RESPONDIDA
let huboVacias = false;
const preguntasVacias = [];

const respNorm = respuestas.map(r => {
  const obj = normalizeKeys(r || {});
  const id = String(obj.pregunta_id || "").trim().toUpperCase();
  const marcadaRaw = String(obj.marcada ?? "").trim().toUpperCase();

  let marcada = marcadaRaw;
  if (marcadaRaw === "") {
    marcada = "NO_RESPONDIDA";
    huboVacias = true;
    if (id) preguntasVacias.push(id);
  }

  return { pregunta_id: id, marcada };
});

// Log seguro (anonimizado) + marca CA2
console.log("[HU2] Normalize OK", {
  email_mask: email.replace(/(.).+(@.*)/, "$1***$2"),
  total: respNorm.length,
  count_vacias: preguntasVacias.length,
  pregunta_vacia_normalizada: huboVacias,
  preguntas_vacias_ids: preguntasVacias
});

// Salida limpia y estandarizada (incluye meta para evidencias de CA2)
return [{
  json: {
    alumno_nombre: nombre.toUpperCase(),
    alumno_email: email.toLowerCase(),
    respuestas: respNorm,
    _meta: {
      valid: true,
      pregunta_vacia_normalizada: huboVacias,
      preguntas_vacias_ids: preguntasVacias,
      count_vacias: preguntasVacias.length
    }
  }
}];

// HU3 - MAP_LogBK: Lectura BK con advertencia y trace (robusto ante huecos)
const startedAt = Date.now();

const rowsRaw = $items().map(i => i.json || {});

// Filtra: descarta objetos vacíos y filas sin pregunta_id útil
const rows = rowsRaw.filter(r => {
  const hasKeys = Object.keys(r).length > 0;
  const pid = String(r.pregunta_id || "").trim();
  return hasKeys && pid !== "";
});

const baseTrace = Array.isArray($json?.__trace) ? $json.__trace : [];
const traceNote = (msg, extra = {}) => ({
  step: "HU3",
  msg,
  at: new Date().toISOString(),
  ...extra
});

console.log("[HU3] BK filas (raw/útiles):", rowsRaw.length, "/", rows.length);

// Si no hay filas útiles: advertencia base_vacia
if (!rows.length) {
  const elapsed = Date.now() - startedAt;
  const trace = baseTrace.concat(
    traceNote("base_vacia", { rows: 0, time_ms: elapsed })
  );
  return [{
    json: {
      bk: [],
      advertencia: "base_vacia",
      warning: "Base de conocimiento vacía (o sin pregunta_id).",
      _meta: { rows: 0, time_ms: elapsed },
      __trace: trace
    }
  }];
}

// Si hay filas, emite cada una. Al primer item adjunta trace.
const total = rows.length;
return rows.map((r, idx) => {
  const out = { ...r };
  if (idx === 0) {
    const elapsed = Date.now() - startedAt;
    out.__trace = baseTrace.concat(
      traceNote("bk_ok", { rows: total, time_ms: elapsed })
    );
  }
  return { json: out };
});

// HU4 - Comparar Respuestas (robusto)
// REQUIERE: "Run Once for All Items" = ON

// --- Debug snapshot (déjalo mientras depuras) ---
const dbg = $input.all().map((i, idx) => ({ idx, keys: Object.keys(i.json) }));
console.log("[HU4] inputs snapshot:", dbg);
// -----------------------------------------------

function flatAllInputs() {
  const arr = $input.all().map(i => i.json);
  const out = [];
  for (const x of arr) {
    if (Array.isArray(x)) out.push(...x);
    else out.push(x);
  }
  return out;
}

function tryExtractPayload(obj) {
  if (!obj || typeof obj !== 'object') return null;
  const candidates = [obj, obj.body, obj.query, obj.formData].filter(Boolean);
  for (const c of candidates) {
    if (Array.isArray(c.respuestas) && (c.alumno_email || c.alumno_nombre)) return c;
  }
  return null;
}

// 1) Reunir todo
const all = flatAllInputs();

// 2) Separar payload vs BK
let payload = null;
const bkRows = [];

for (const item of all) {
  if (item && typeof item.pregunta_id === 'string') {
    bkRows.push(item);
    continue;
  }
  const candidate = tryExtractPayload(item);
  if (candidate && !payload) payload = candidate;
}

if (!payload) {
  return [{
    json: {
      error: "NO_PAYLOAD",
      message: "No llegó payload normalizado de HU2",
      _meta: { step: "HU4", inputs: all.length }
    }
  }];
}

if (!bkRows.length) {
  const desconocidas = (payload.respuestas || []).length;
  return [{
    json: {
      alumno_nombre: payload.alumno_nombre,
      alumno_email:  payload.alumno_email,
      totales: {
        total_preguntas: desconocidas,
        aciertos: 0, errores: 0, omitidas: 0, desconocidas,
        total_puntaje: 0, puntaje_obtenido: 0
      },
      desglose: (payload.respuestas || []).map(r => ({
        pregunta_id: String(r.pregunta_id||"").toUpperCase(),
        marcada: String(r.marcada||"").toUpperCase(),
        estado: "PREGUNTA_NO_ENCONTRADA",
        correcta: null, puntaje: 0, explicacion: "Sin BK"
      })),
      warning: "BK vacía o no disponible"
    }
  }];
}

// 3) Mapa BK
const bkMap = new Map();
for (const row of bkRows) {
  const id = String(row.pregunta_id || "").trim().toUpperCase();
  const correcta = String(row.correcta || "").trim().toUpperCase();
  const explicacion = String(row.explicacion || "").trim();
  const puntaje = Number(row.puntaje ?? 1);
  if (id) bkMap.set(id, { correcta, explicacion, puntaje: isNaN(puntaje) ? 1 : puntaje });
}

// 4) Comparación
let aciertos = 0, errores = 0, omitidas = 0, desconocidas = 0;
let total_puntaje = 0, puntaje_obtenido = 0;
const desglose = [];

const respuestas = Array.isArray(payload.respuestas) ? payload.respuestas : [];
for (const r of respuestas) {
  const id = String(r.pregunta_id || "").toUpperCase();
  const marcada = String(r.marcada || "").toUpperCase();

  if (!bkMap.has(id)) {
    desconocidas++;
    desglose.push({
      pregunta_id: id, marcada,
      estado: "PREGUNTA_NO_ENCONTRADA",
      correcta: null, explicacion: "Pregunta no existe en BK",
      puntaje: 0
    });
    continue;
  }

  const { correcta, explicacion, puntaje } = bkMap.get(id);
  total_puntaje += puntaje;

  if (marcada === "NO_RESPONDIDA") {
    omitidas++;
    desglose.push({ pregunta_id: id, marcada, estado: "NO_RESPONDIDA", correcta, explicacion, puntaje });
    continue;
  }

  const esCorrecta = (marcada === correcta);
  if (esCorrecta) { aciertos++; puntaje_obtenido += puntaje; }
  else { errores++; }

  desglose.push({
    pregunta_id: id, marcada, correcta,
    es_correcta: esCorrecta,
    estado: esCorrecta ? "CORRECTA" : "INCORRECTA",
    explicacion, puntaje
  });
}

const total_preguntas = respuestas.length;

console.log("[HU4] Comparación OK", {
  total_preguntas, aciertos, errores, omitidas, desconocidas,
  total_puntaje, puntaje_obtenido
});

return [{
  json: {
    alumno_nombre: payload.alumno_nombre,
    alumno_email:  payload.alumno_email,
    totales: { total_preguntas, aciertos, errores, omitidas, desconocidas, total_puntaje, puntaje_obtenido },
    desglose
  }
}];

const t = $json.totales || {};
console.log("[HU4] Totales", t);
return [{ json: $json }];

// HU5 - Cálculo de nota ponderada + validaciones negativas
// Entrada esperada (desde HU4):
// { alumno_nombre, alumno_email,
//   totales: { total_preguntas, aciertos, errores, omitidas, desconocidas, total_puntaje, puntaje_obtenido },
//   desglose: [...],
//   __trace?: [] }

const inJson = $json || {};
const trace = Array.isArray(inJson.__trace) ? inJson.__trace : [];

const tot = inJson.totales || {};
const obtenido = Number(tot.puntaje_obtenido || 0);
const total    = Number(tot.total_puntaje || 0);
const totalPreg = Number(tot.total_preguntas || 0);
const omitidas  = Number(tot.omitidas || 0);

// ❌ CA3 – Puntaje inválido: total_puntaje = 0
if (!Number.isFinite(total) || total <= 0) {
  const err = { code: "puntaje_invalido", message: "Total de puntaje = 0. Nota incalculable." };
  trace.push({ step: "HU5", msg: "puntaje_invalido", at: new Date().toISOString(), details: { total } });
  return [{ json: { ...inJson, error: err, _meta: { step: "HU5" }, __trace: trace } }];
}

// ❌ NUEVO: CAx – Sin respuestas: todas fueron NO_RESPONDIDA
const allOmitidas = totalPreg > 0 && omitidas === totalPreg;
if (allOmitidas) {
  const err = { code: "sin_respuestas", message: "Todas las preguntas fueron omitidas. Nota incalculable." };
  trace.push({ step: "HU5", msg: "sin_respuestas", at: new Date().toISOString(), details: { totalPreg, omitidas } });
  return [{ json: { ...inJson, error: err, _meta: { step: "HU5" }, __trace: trace } }];
}

// ✅ Cálculo normal
const porcentaje = (obtenido / total) * 100;
const notaRaw = 1 + 6 * (porcentaje / 100);
const nota = Math.round(notaRaw * 10) / 10;
const aprobado = porcentaje >= 60;
const porcentaje1d = Math.round(porcentaje * 10) / 10;

const resultado = {
  alumno_nombre: inJson.alumno_nombre,
  alumno_email:  inJson.alumno_email,
  porcentaje: porcentaje1d,
  nota,
  aprobado,
  mensaje: aprobado ? "Aprobado (≥60%)" : "Reprobado (<60%)",
  puntaje: { obtenido, total },
};

trace.push({ step: "HU5", msg: "resultado_ok", at: new Date().toISOString(), resultado });

return [{ json: { ...inJson, resultado, __trace: trace } }];

const r = $json.resultado || {};
console.log("[HU5] OUT_Resultado", r);
return [{ json: $json }];
